class PrivacyTermsPage extends StatefulWidget {
  String termName;

  PrivacyTermsPage({Key key, this.termName}) : super(key: key);

  @override
  _PrivacyTermsPageState createState() => new _PrivacyTermsPageState();
}

class _PrivacyTermsPageState extends State<PrivacyTermsPage> {
  String content = '';

  void initState() {
    super.initState();
    this.loadAsset((String content) {
      setState(() {
        this.content = content;
      });
    });
  }

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
  }

  Future<String> loadAsset(callback) async {
    if ('privacy' == this.widget.termName) {
      var content = await rootBundle.loadString('resource/file/privateProtocol.txt');
      callback(content);
    } else if ('service' == this.widget.termName) {
      var content = await rootBundle.loadString('resource/file/serviceProtocol.txt');
      callback(content);
    }
  }

  @override
  Widget build(BuildContext context) {
    return new Scaffold(
      backgroundColor: Colors.transparent,
      body: SingleChildScrollView(
        child: new Container(
          width: ScreenUtil().setWidth(1080),
          height: ScreenUtil().setHeight(1920),
          decoration: BoxDecoration(
            image: DecorationImage(
              image: AssetImage("resource/images/loginBackground.png"),
              fit: BoxFit.cover,
            ),
          ),
          child: new Container(
            margin: EdgeInsets.fromLTRB(ScreenUtil().setWidth(100), ScreenUtil().setHeight(600), ScreenUtil().setWidth(100), ScreenUtil().setHeight(100)),
            child: Column(
              children: <Widget>[
                Container(
                  height: ScreenUtil().setHeight(1000),
                  child: SingleChildScrollView(
                    child: Text(content, style: TextStyle(fontSize: ScreenUtil().setSp(32), color: Colors.white, decoration: TextDecoration.none)),
                  ),
                ),
                new ImageButton(
                  height: ScreenUtil().setHeight(SystemButtonSize.largeButtonHeight),
                  width: ScreenUtil().setWidth(SystemButtonSize.largeButtonWidth),
                  buttonName: "返回",
                  imageUrl: "resource/images/upgradeButton.png",
                  callback: () {
                    Application.router.pop(context);
                  },
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }

